@Controller('users')
export class UsersController {
  @Post()
  async create(@Body() data: any) {
    return this.prisma.user.create({ data });
  }
}

@Injectable()
export class OrdersService {
  async processOrder(id: string) {
    await this.prisma.order.update({ where: { id }, data: { status: 'processing' } });
    await this.prisma.payment.create({ data: { orderId: id } });
  }
}

@Injectable()
export class CustomersService {
  async getCustomers() {
    return this.prisma.customer.findMany();
  }
}

@Injectable()
export class WorkOrdersService {
  async create(data: any) {
    try {
      const result = await this.prisma.workOrder.create({ data });
      this.logger.info('Work order created');
      return result;
    } catch (error) {
      throw error;
    }
  }
}
